  import React, { useState } from 'react';
import {
  Text,
  View,
  StyleSheet,
  SafeAreaView,
  TouchableOpacity,
  Button,
} from 'react-native';
import { TextInput,ScrollView } from 'react-native';
import Constants from 'expo-constants';
import styled from 'styled-components/native';
import { Ionicons } from '@expo/vector-icons';
import { MaterialIcons } from '@expo/vector-icons';
import { MaterialCommunityIcons } from '@expo/vector-icons';
import { Entypo } from '@expo/vector-icons';
import { Feather } from '@expo/vector-icons';
import { AntDesign } from '@expo/vector-icons';
export const SignupUser3 = ({ route, navigation }) => {
  const [type, Settype] = useState('');
  const [rate, Setrate] = useState('');
  const [working_hours, Setworking_hours] = useState('');
  const [off_days, Setoff_days] = useState('');
  const [land_line, Setland_line] = useState('');
  const [data2, setData2] = React.useState({
    isRequiredType: true,
    isRequiredRate: true,
    isRequiredWorking_hours: true,
    isRequiredOff_days: true,
    isRequiredLand_line: true,
  });
  const { username, phone, email, password, confirm_password, nid, address,country,city,isValidUser,
      isValidServiceProvider, } = route.params;
  /* console.log(username, phone, email, password, confirm_password, nid, address,country,city,  isValidUser,
      isValidServiceProvider,);*/
 const handelRequiredType = (val) => {
    if (val === '') {
      setData2({
        ...data2,
        isRequiredType: false,
      });
    } else {
      setData2({
        ...data2,
        isRequiredType: true,
      });
    }
  };
   const handelRequiredRate = (val) => {
    if (val === '') {
      setData2({
        ...data2,
        isRequiredRate: false,
      });
    } else {
      setData2({
        ...data2,
        isRequiredRate: true,
      });
    }
  };
  const handelRequiredWorking_hours = (val) => {
    if (val === '') {
      setData2({
        ...data2,
        isRequiredWorking_hours: false,
      });
    } else {
      setData2({
        ...data2,
        isRequiredWorking_hours: true,
      });
    }
  };
  const handelRequiredOff_days = (val) => {
    if (val === '') {
      setData2({
        ...data2,
        isRequiredOff_days: false,
      });
    } else {
      setData2({
        ...data2,
        isRequiredOff_days: true,
      });
    }
  };

  const handelRequiredLand_line = (val) => {
    if (val === '') {
      setData2({
        ...data2,
        isRequiredLand_line: false,
      });
    } else {
      setData2({
        ...data2,
        isRequiredLand_line: true,
      });
    }
  };
 function Next(){
   if(data2.isRequiredType===true && data2.isRequiredRate===true && data2.isRequiredWorking_hours===true && data2.isRequiredOff_days===true && data2.isRequiredLand_line===true){
     navigation.navigate('Signup4', {
            username: username,
            phone: phone,
            email: email,
            password: password,
            confirm_password: confirm_password,
            nid: nid,
            country: country,
            city: city,
            address: address,
            type:type,
            rate:rate,
            working_hours:working_hours,
            off_days:off_days,
            land_line:land_line,
            isValidUser:isValidUser,
            isValidServiceProvider:isValidServiceProvider,
     })
  }
 }
  return (
   <View style={styles.container1}>
      <View style={styles.input}>
        <TextInput
          style={styles.inputtext1}
          placeholder="Enter Type"
          onChangeText={(type) => Settype(type)}
          onEndEditing={(e) => handelRequiredType(e.nativeEvent.text)}
          placeholderTextColor="gray"
        />
      </View>
      {data2.isRequiredType? null :
        <View style={styles.error}>
          <Text style={styles.errormsg}>
             Reuired Input
          </Text>
        </View>
      }
      <View style={styles.input}>
        <TextInput
          style={styles.inputtext1}
          placeholder="Enter Rate"
          onChangeText={(rate) => Setrate(rate)}
          onEndEditing={(e) => handelRequiredRate(e.nativeEvent.text)}
          placeholderTextColor="gray"
        />
      </View>
      {data2.isRequiredRate ? null :
        <View style={styles.error}>
          <Text style={styles.errormsg}>Reuired Input</Text>
        </View>
      }
      <View style={styles.input}>
        <TextInput
          style={styles.inputtext1}
          placeholder="Enter Working_Hours"
          onChangeText={(working_hours) => Setworking_hours(working_hours)}
          onEndEditing={(e) => handelRequiredWorking_hours(e.nativeEvent.text)}
          placeholderTextColor="gray"
        />
      </View>
       {data2.isRequiredWorking_hours? null :
        <View style={styles.error}>
          <Text style={styles.errormsg}>Reuired Input</Text>
        </View>
      }
      <View style={styles.input}>
        <TextInput
          style={styles.inputtext1}
          placeholder="Enter Off_Days"
          onChangeText={(off_days) => Setoff_days(off_days)}
          onEndEditing={(e) => handelRequiredOff_days(e.nativeEvent.text)}
          placeholderTextColor="gray"
        />
      </View>
      {data2.isRequiredOff_days ? null :
        <View style={styles.error}>
          <Text style={styles.errormsg}>Reuired Input</Text>
        </View>
       }

       <View style={styles.input}>
        <TextInput
          style={styles.inputtext1}
          placeholder="Enter Land_Line"
          onChangeText={(land_line) => Setland_line(land_line)}
          onEndEditing={(e) => handelRequiredLand_line(e.nativeEvent.text)}
          placeholderTextColor="gray"
        />
      </View>
      {data2.isRequiredLand_line ? null :
        <View style={styles.error}>
          <Text style={styles.errormsg}>Reuired Input</Text>
        </View>
       }
      <TouchableOpacity
        style={styles.buttoncontainer1}
        onPress={Next}>
        <Text style={styles.buttontext1}>Next</Text>
      </TouchableOpacity>
   </View>
  );
};
const styles = StyleSheet.create({
  container1: {
    backgroundColor: 'white',
    flex:1,
    justifyContent: 'center',
    alignItems: 'center',
     paddingTop:120,
    paddingBottom:60,
  },
  input: {
    flexDirection: 'row',
  },
  inputtext1: {
    borderRadius: 9,
    width: 300,
    height: 40,
    borderColor: '#ff00ff',
    borderWidth: 1,
    paddingHorizontal: 10,
    fontSize: 30,
    color: 'black',
    marginVertical: 10,
  },
  errormsg: {
    bottom: 5,
    paddingRight: 199,
    color: 'red',
  },
  buttoncontainer1: {
    borderRadius: 15,
    width: 150,
    height: 45,
    backgroundColor: '#ff00ff',
    paddingTop: 1,
    justifyContent: 'center',
    marginTop: 30,
    paddingLeft: 60,
  },
  buttontext1: {
    textAlign: 'center',
    color: 'black',
    fontSize: 20,
    right: 30,
  },
});
