import React, { useState, useContext } from "react";
import {
  Text,
  View,
  StyleSheet,
  SafeAreaView,
  StatusBar,
  ScrollView,
  TouchableOpacity,
} from "react-native";
import { TextInput, Platform } from "react-native";
import axios from "axios";

export const AccountVerfication = ({ navigation }) => {
  const [verifyCode, SetverifyCode] = useState("");

  function confirm() {
    axios
      .patch(
        `https://petzone99.herokuap
p.com/api/v1/users/emailVerfication/${verifyCode}`,
        {
          headers: {
            "Content-Type": "application/json",
            "My-Custom-Header": "foobar",
            Authorization: `Bearer ${verifyCode}`,
          },
        }
      )
      .then(function (response) {
        navigation.navigate("Home");
      })
      .catch(function (error) {
        if (error.response.status === 400) {
          alert("Token is invalid or has expired ,please login again");
          navigation.navigate("Login");
        }
      });
  }
  return (
    <ScrollView style={styles.view}>
      <View style={styles.container}>
        <Text style={styles.title}>Account Verfication</Text>
        <View style={styles.input}>
          <TextInput
            style={styles.inputtext}
            placeholder="Enter VerficationCode"
            onChangeText={(verifyCode) => SetverifyCode(verifyCode)}
            placeholderTextColor="gray"
          />
        </View>
        <TouchableOpacity style={styles.buttoncontainer1} onPress={confirm}>
          <Text style={styles.buttontext1}>Confirm</Text>
        </TouchableOpacity>
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    justifyContent: "center",
    alignItems: "center",
  },
  title: {
    color: "black",
    fontSize: 25,
    alignItems: "center",
    paddingBottom: 20,
  },
  view: {
    backgroundColor: "white",
    flex: 1,
    paddingTop: 200,
  },
  icon: {
    paddingBottom: 35,
    marginTop: 5,
  },
  input: {
    paddingTop: 5,
  },
  inputtext: {
    borderRadius: 9,
    width: 300,
    height: 40,
    borderColor: "#ff00ff",
    borderWidth: 1,
    paddingHorizontal: 10,
    fontSize: 30,
    color: "black",
    marginVertical: 10,
  },
  buttoncontainer1: {
    borderRadius: 15,
    width: 150,
    height: 45,
    backgroundColor: "#ff00ff",
    paddingTop: 1,
    justifyContent: "center",
    marginTop: 15,
    paddingLeft: 60,
  },
  errormsg: {
    bottom: 5,
    paddingRight: 199,
    color: "red",
  },
  buttontext1: {
    textAlign: "center",
    color: "black",
    fontSize: 20,
    right: 30,
  },
});
