import React, { useState } from "react";
import {
  Text,
  View,
  StyleSheet,
  SafeAreaView,
  TouchableOpacity,
  Button,
} from "react-native";
import { TextInput } from "react-native";
import styled from "styled-components/native";
import { Ionicons } from "@expo/vector-icons";
import { MaterialIcons } from "@expo/vector-icons";
import { MaterialCommunityIcons } from "@expo/vector-icons";
import { Feather } from "@expo/vector-icons";
import { Octicons } from "@expo/vector-icons";
import axios from "axios";

export const Forgetpassword = ({ navigation }) => {
  const [data, setData] = React.useState({
    isValidEmail: true,
  });
  const handleValidus = (val) => {
    let reg = /@./;
    if (val.trim().length >= 4 && reg.test(val) !== false) {
      setData({
        ...data,
        isValidEmail: true,
      });
    } else {
      setData({
        ...data,
        isValidEmail: false,
      });
    }
  };

  const [email, SetEamil] = useState("");

  async function Login1() {
    let item = { email };
    await axios
      .post("https://petzone99.herokuap
p.com/api/v1/users/forgotPassword", item, {
        headers: {
          "Content-Type": "application/json",
          "My-Custom-Header": "foobar",
          Authorization: "Bearer my-token",
        },
      })
      .then(function (response) {
        navigation.navigate("Verificationpasswordcode");
      })
      .catch(function (error) {
        if (error.response.status === 404) {
          alert("There is no user with this email address.");
        } else if (error.response.status === 500) {
          alert("There was an error sending the email. Try again later!");
        }
      });
  }
  function Emilvalidator() {
    if (email == "")
      (emailError) => SetEamilError("emil feild can not be empty");
  }
  return (
    <View style={styles.container}>
      {/* {<Text style={styles.title}>Login</Text>} */}

      <Octicons
        style={styles.icon}
        name="mail-read"
        size={125}
        color="rgb(255, 89, 89)"
      />

      <View style={styles.input}>
        <TextInput
          style={styles.inputtext}
          placeholder="Enter your Email"
          placeholderTextColor="gray"
          value={email}
          onEndEditing={(e) => handleValidus(e.nativeEvent.text)}
          onBlur={Emilvalidator}
          onChangeText={(email) => SetEamil(email)}
        />
        <MaterialCommunityIcons
          style={styles.icon1}
          name="email-outline"
          size={24}
          color="gray"
        />
      </View>
      {data.isValidEmail ? null : (
        <Text style={{ color: "red" }}> email must have '@' and '.'</Text>
      )}

      <TouchableOpacity
        style={styles.buttoncontainer}
        onPress={Login1}
        // onPress={() => navigation.navigate('TypeSignup')}
        /**/
      >
        <Text style={styles.buttontext}>Send Email</Text>
      </TouchableOpacity>
    </View>
  );
};
const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "rgb(245, 245, 245)",
  },
  input: {
    flexDirection: "row",
    width: 300,
  },
  signupText: {
    paddingRight: 10,
    fontSize: 15,
    marginRight: 10,
  },
  title: {
    color: "black",
    fontSize: 20,
    alignItems: "center",
    marginBottom: 30,
  },

  inputtext: {
    borderRadius: 9,
    width: 300,
    height: 40,
    borderColor: "rgb(48, 71, 94)",
    borderWidth: 1,
    paddingHorizontal: 35,
    fontSize: 15,
    color: "black",
    marginVertical: 10,
  },
  icon1: {
    position: "absolute",
    top: 18,
    left: 5,
  },
  signuptextCont: {
    flex: 0,
    alignItems: "center",
    justifyContent: "flex-end",
    paddingVertical: 1,
    flexDirection: "row",
    marginTop: 30,
  },
  buttoncontainer: {
    borderRadius: 15,
    width: 150,
    height: 40,
    backgroundColor: "rgb(255, 89, 89)",
    paddingTop: 1,
    justifyContent: "center",
    marginTop: 15,
    paddingLeft: 60,
  },
  buttontext: {
    textAlign: "center",
    color: "black",
    fontSize: 15,
    right: 30,
  },
  buttontext1: {
    textAlign: "center",
    color: "rgb(221, 74, 72)",
    fontSize: 15,
    right: 25,
  },
  logo: {
    height: 128,
    width: 128,
    marginBottom: 30,
  },
});
