import React from "react";
import {
  Text,
  View,
  StyleSheet,
  SafeAreaView,
  TouchableOpacity,
  Button,
  Image,
} from "react-native";
import axios from "axios";

export const TypeSignup = ({ route, navigation }) => {
  const role = "";

  const {
    name,
    userName,
    phoneNumber,
    email,
    password,
    passwordConfirm,
    nationalID,
    address,
    country,
    city,
    image,
  } = route.params;

  function confirm() {
    axios({
      method: "post",
      url: "https://petzone99.herokuap
p.com/api/v1/users/verifyEmail",
      data: {
        email,
      },
    })
      .then(function (response) {
        navigation.navigate("AccountVerfication");
      })
      .catch(function (error) {});
  }

  function signup() {
    let user = {
      name: name,
      email: email,
      password: password,
      passwordConfirm: passwordConfirm,
      userName: userName,
      nationalID: nationalID,
      city: city,
      country: country,
      address: address,
      phoneNumber: phoneNumber,
      profilePicture: image,
      role: "pet owner",
      verified: false,
    };
    axios
      .post("https://petzone99.herokuap
p.com/api/v1/users/signup", user, {
        headers: {
          "Content-Type": "application/json",
          "My-Custom-Header": "foobar",
          Authorization: "Bearer my-token",
        },
      })
      .then(function (response) {
        confirm();
      })
      .catch(function (error) {
        console.log(error); 
      });
  }
  return (
    <View style={styles.container1}>
       <Image style={styles.imagestyle1} source={{uri:'https://cdn-icons-png.flaticon.com/512/3135/3135715.png'}} />
      <View style = {styles.user}>
      <TouchableOpacity style={styles.buttoncontainer1} onPress={signup}>
        <Text style={styles.buttontext1}>Signup as user</Text>
      </TouchableOpacity>
      </View>
      <Image style={styles.imagestyle} source={{uri:'https://cdn-icons-png.flaticon.com/512/4370/4370928.png'}} />
     <View style = {styles.sp}>
     <TouchableOpacity
        style={styles.buttoncontainer1}
        onPress={() =>
          navigation.navigate("SP1", {
            name: name,
            email: email,
            password: password,
            passwordConfirm: passwordConfirm,
            userName: userName,
            nationalID: nationalID,
            city: city,
            country: country,
            address: address,
            phoneNumber: phoneNumber,
            profilePicture: image,
            role: "service provider",
          })
        }
      >
        <Text style={styles.buttontext1}>Signup as service provider</Text>
      </TouchableOpacity>
     </View>
      
    </View>
  );
};
const styles = StyleSheet.create({
  container1: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    paddingBottom: 50,
    backgroundColor: "rgba(253,239,197,1)",
  },
  user:{
  marginRight:190,
  top:100
  },
  imagestyle:{
    width:200,
    height:160,
    marginLeft:200,
    bottom:160,
    resizeMode: 'contain'
  },
  imagestyle1:{
    width:200,
    height:160,
    marginRight:190,
    top:90,
    resizeMode: 'contain'
  },
  sp:{
  marginLeft:195,
  bottom:150
  },
  input1: {
    flexDirection: "row",
  },

  icon1: {
    position: "absolute",
    top: 18,
    left: 5,
  },
  buttoncontainer1: {
    borderRadius: 15,
    width: 180,
    height: 88,
    backgroundColor: "#ED7354",
    paddingTop: 1,
    justifyContent: "center",
    marginTop: 2,
    paddingLeft: 60,
  },
  buttontext1: {
    textAlign: "center",
    color: "white",
    fontSize: 20,
    right: 30,
  
  },
});
