import React from "react";
import {
  Text,
  View,
  StyleSheet,
  SafeAreaView,
  TouchableOpacity,
  Button,
} from "react-native";
import axios from "axios";

export const TypeSignup = ({ route, navigation }) => {
  const role = "";

  const {
    name,
    username,
    phoneNumber,
    email,
    password,
    passwordConfirm,
    nationalID,
    address,
    country,
    city,
    image,
  } = route.params;

  async function signup() {
    let user = {
      name: name,
      email: email,
      password: password,
      passwordConfirm: passwordConfirm,
      userName: username,
      nationalID: nationalID,
      city: city,
      country: country,
      address: address,
      phoneNumber: phoneNumber,
      profilePicture: image,
      role: "pet owner",
    };

    await axios
      .post("https://petzone99.herokuap
p.com/api/v1/users/signup", user, {
        headers: {
          "Content-Type": "application/json",
          "My-Custom-Header": "foobar",
          Authorization: "Bearer my-token",
        },
        
      })
      .then(function (response) {
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });

  }
return (
  <View style={styles.container1}>
    <TouchableOpacity style={styles.buttoncontainer1} onPress={signup}>
      <Text style={styles.buttontext1}>Signup as user</Text>
    </TouchableOpacity>

    <TouchableOpacity
      style={styles.buttoncontainer1}
      onPress={() =>
        navigation.navigate("SP1", {
          name: name,
          email: email,
          password: password,
          passwordConfirm: passwordConfirm,
          userName: username,
          nationalID: nationalID,
          city: city,
          country: country,
          address: address,
          phoneNumber: phoneNumber,
          profilePicture: image,
          role: "service provider",
        })
      }
    >
      <Text style={styles.buttontext1}>Signup as service provider</Text>
    </TouchableOpacity>
  </View>
);
    }
const styles = StyleSheet.create({
  container1: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    paddingBottom: 50,
    backgroundColor: "rgb(245, 245, 245)",
  },
  input1: {
    flexDirection: "row",
  },
  inputtext1: {
    borderRadius: 9,
    width: 300,
    height: 40,
    borderColor: "rgb(48, 71, 94)",
    borderWidth: 1,
    paddingHorizontal: 35,
    fontSize: 15,
    color: "black",
    marginVertical: 10,
  },
  icon1: {
    position: "absolute",
    top: 18,
    left: 5,
  },
  buttoncontainer1: {
    borderRadius: 15,
    width: 300,
    height: 45,
    backgroundColor: "rgb(240, 84, 84)",
    paddingTop: 1,
    justifyContent: "center",
    marginTop: 30,
    paddingLeft: 60,
  },
  buttontext1: {
    textAlign: "center",
    color: "black",
    fontSize: 20,
    right: 30,
  },
})
