import React, { useState, useEffect } from "react";
import {
  Image,
  View,
  Platform,
  TouchableOpacity,
  Text,
  StyleSheet,
  Button,
} from "react-native";
import { AntDesign } from "@expo/vector-icons";
import styled from "styled-components/native";
import * as ImagePicker from "expo-image-picker";

import axios from "axios";

export const SPFinal = ({ route, navigation }) => {
  const [image, setImage] = useState(null);
  const {
    name,
    username,
    phoneNumber,
    email,
    password,
    passwordConfirm,
    nationalID,
    country,
    city,
    address,
    role,
    type,
    rating,
    workingHours,
    finishingHour,
    maxNumberClients,
    offDays,
    ratePerHour,
    landLine,
    verificationDocuments,
  } = route.params;
  const addImage = async () => {
    let _image = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: true,
      aspect: [4, 3],
      quality: 1,
    });
    if (!_image.cancelled) {
      setImage(_image.uri);
    }
  };
  async function signup() {
    let user = {
      name: name,
      email: email,
      password: password,
      passwordConfirm: passwordConfirm,
      userName: username,
      nationalID: nationalID,
      city: city,
      country: country,
      address: address,
      phoneNumber: phoneNumber,
      role: role,
      profilePicture: image,
      serviceProvider: {
        type: type,
        rating: rating,
        workingHours: workingHours,
        finishingHour: finishingHour,
        maxNumberClients: maxNumberClients,
        offDays: offDays,
        ratePerHour: ratePerHour,
        landLine: landLine,
        verificationDocuments: verificationDocuments,
      },
    };
    console.warn(user);
    await axios
      .post("https://petzone99.herokuap
p.com/api/v1/users/signup", user, {
        headers: {
          "Content-Type": "application/json",
          "My-Custom-Header": "foobar",
          Authorization: "Bearer my-token",
        },
      })
      .then(function (response) {
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });
    navigation.navigate("Home");
  }
  return (
    <View style={styles.container2}>
      <Text style={styles.input}> Adding Profile Picture</Text>
      <View style={imageUploaderStyles.container}>
        {image && (
          <Image source={{ uri: image }} style={{ width: 200, height: 200 }} />
        )}
        <View style={imageUploaderStyles.uploadBtnContainer}>
          <TouchableOpacity
            onPress={addImage}
            style={imageUploaderStyles.uploadBtn}
          >
            <Text>{image ? "Edit" : "Upload"} Image</Text>
            <AntDesign name="camera" size={20} color="black" />
          </TouchableOpacity>
        </View>
      </View>
      <TouchableOpacity style={styles.buttoncontainer1} onPress={signup}>
        <Text style={styles.buttontext1}>SignUp</Text>
      </TouchableOpacity>
    </View>
  );
};
const styles = StyleSheet.create({
  container2: {
    flex: 1,
    backgroundColor: "rgb(245, 245, 245)",
    marginBottom: 0,
    alignItems: "center",
    backgroundColor: "white",
  },
  input: {
    color: "black",
    top: 1,
    textAlign: "center",
    fontSize: 20,
    marginTop: 90,
  },

  buttoncontainer1: {
    borderRadius: 15,
    width: 150,
    height: 45,
    backgroundColor: "rgb(240, 84, 84)",
    paddingTop: 1,
    justifyContent: "center",
    marginTop: 60,
    paddingLeft: 60,
  },
  buttontext1: {
    textAlign: "center",
    borderColor: "rgb(48, 71, 94)",
    fontSize: 15,
    right: 25,
  },
  signuptext: {
    textAlign: "center",
    color: "black",
    fontSize: 18,
    flexDirection: "row",
    marginTop: 20,
  },
});
const imageUploaderStyles = StyleSheet.create({
  container: {
    elevation: 2,
    height: 200,
    width: 200,
    top: 30,
    backgroundColor: "#efefef",
    position: "relative",
    borderRadius: 999,
    overflow: "hidden",
  },
  uploadBtnContainer: {
    opacity: 0.7,
    position: "absolute",
    right: 0,
    bottom: 0,
    backgroundColor: "lightgray",
    width: "100%",
    height: "25%",
  },
  uploadBtn: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },
});
